/*Table structure for table `mm_blog_list` */

DROP TABLE IF EXISTS `mm_blog_list`;

CREATE TABLE `mm_blog_list` (
  `ID` bigint(20) NOT NULL AUTO_INCREMENT,
  `BLOG_TITLE` varchar(255) DEFAULT NULL COMMENT '标题',
  `IMG_PATH` varchar(255) DEFAULT NULL COMMENT '图片路径',
  `BLOG_CONTENT` varchar(4000) DEFAULT NULL COMMENT '内容',
  `BLOG_AUTHOR` varchar(255) DEFAULT NULL COMMENT '作者',
  `create_time` date DEFAULT NULL COMMENT '创建时间',
  `blog_type` varchar(20) DEFAULT NULL COMMENT '博客类型',
  `transfer_path` varchar(255) DEFAULT NULL COMMENT '转载地址',
  `last_update_time` date DEFAULT NULL COMMENT '最后更新时间',
  PRIMARY KEY (`ID`),
  KEY `idx_bloglist_title` (`BLOG_TITLE`(191))
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

/*Data for the table `mm_blog_list` */

insert DELAYED  into `mm_blog_list`(`ID`,`BLOG_TITLE`,`IMG_PATH`,`BLOG_CONTENT`,`BLOG_AUTHOR`,`create_time`,`blog_type`,`transfer_path`,`last_update_time`) values (2,'沉默的十五分钟','images/kenan-chenmode15fenzhong.jpg','《名侦探柯南：沉默的15分钟》是根据日本漫画家青山刚昌创作的漫画《名侦探柯南》改编的系列动画电影中的第15部作品。影片讲述了柯南为了查明地铁隧道爆炸案和在雪原发现尸体的案件之间关联而展开巧妙推理的故事。影片于2011年4月16日在日本首映，2011年7月8日起在中国台湾地区上映，2011年11月4日起在中国大陆地区上映。此影片是《名侦探柯南》系列动画电影15周年纪念作品。','青山刚昌','2017-08-09',NULL,'blog/shouye/shouye.jsp','2017-08-09'),(3,'贝克街的亡灵','images/kenan-beikejiedewangling.jpg','《名侦探柯南：贝克街的亡灵》是根据日本漫画家青山刚昌创作的漫画《名侦探柯南》改编的系列动画电影的第6部作品。影片讲述了少年侦探柯南挑战虚拟体感游戏过程中追寻解开杀人事件真相的线索并活跃行动的故事。影片于2002年4月20日在日本首映。该影片曾位列名侦探柯南剧场版历代19部作品人气投票最终结果第2位。','青山刚昌','2017-08-09',NULL,'blog/shouye/shouye.jsp','2017-08-09'),(4,'小米联合创始人反思：我们吃过的亏，补过的课，看到的机会','images/xm11.png','我们做生态链时，除了产品以外，我们超级重视这个产品能不能带来商业上的成功。所以一件事情能不能靠商业得到成功，并且具有持续性，这是我们非常重视的。我们去见一个行业的老先生，我问他做一家大公司最重...[全文]','刘德','2017-08-29',NULL,'blog/shouye/shouye.jsp','2017-08-30'),(5,'共享单车押金真的难退吗？是技术问题，还是故意为之！','images/59a63d84069ee1504066948.png','昨天是七夕，晚上的时候发现地铁的人流比平时多出了好几倍，这还不重要，关键都是情侣或小两口，并且正在筹划着晚上的活动，这可把一些单身狗虐的苦不堪言！','默认栏目','2017-08-29',NULL,'blog/shouye/shouye.jsp','2017-08-29');

/*Table structure for table `mm_blog_list_leage` */

DROP TABLE IF EXISTS `mm_blog_list_leage`;

CREATE TABLE `mm_blog_list_leage` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `author` varchar(20) DEFAULT NULL,
  `publish_time` date DEFAULT NULL,
  `content` text,
  `blog_list_id` bigint(20) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `blog_list_id` (`blog_list_id`),
  CONSTRAINT `mm_blog_list_leage_ibfk_1` FOREIGN KEY (`blog_list_id`) REFERENCES `mm_blog_list` (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

/*Data for the table `mm_blog_list_leage` */

insert DELAYED  into `mm_blog_list_leage`(`id`,`title`,`author`,`publish_time`,`content`,`blog_list_id`) values (1,'小米联合创始人反思：我们吃过的亏，补过的课，看到的机会','刘德','2017-08-29','\r\n                    <p> 我去美国的时候，我家里人跟我说，这么大年纪了，干吗要到美国去呢？</p>\r\n\r\n                <p> 我老爸跟我说，一代人只干一代人的事情。</p>\r\n\r\n                <p>  我跟他讲，实际上一代人要干两代人的事情，因为我们这一代人落下的东西太多了，我们要补上。\r\n                    美国的设计教育</p>\r\n\r\n                <p>  实用+创新+商业化</p>\r\n\r\n                <h4>第一个特点：实用主义</h4>\r\n\r\n                <p>    我们经常在美国看到一些老房子，都100年了，房子好破，但是门轴、门锁、把手的功能，好得不得了。</p>\r\n\r\n                <p>  他们为什么对产品的基础功能有那么大的追求呢？有一次我开车，从美国最东北方一路开到洛杉矶，斜跨美国。</p>\r\n\r\n                   <p> 这个时候我才理解，在浩瀚的没有人烟的美国中部，万一你的车坏了，你可能就死在那了，连电话的信号都没有，所以自然环境要求他们要超级重视产品的基础功能。\r\n\r\n                    这些对我们后来做生态链产品的定义，有重要的帮助。如果你用到生态链产品，就会发现我们要求所有的产品，它的基础功能都是要过硬的，然后再叠加其他。</p>\r\n\r\n                    <h4>第二个特点：创新</h4>\r\n\r\n                <p> 美国是一个非常尊重创新的国家，因为世界的人尖都涌到那个国家去，所以它有一个创新的氛围。</p>\r\n\r\n                <p>   我们得了大量的红点奖、IF奖等等，大概140多项奖项。但是我们拿不到IDEA奖(美国工业设计杰出大奖 )，它是由美国工业设计师协会评审，给全世界杰出的工业设计作品颁发的大奖。\r\n\r\n                    我们怎么老是得不到这个奖呢？</p>\r\n\r\n                <p>  这个问题卡在了创新上。因为小米的很多产品，它能保证比前代的产品又好又便宜，基础功能又过硬，但是它不鼓励大幅度创新。</p>\r\n\r\n                <p> 为什么不鼓励大幅度创新呢？因为我们每一款产品都卖几百万个，几千万个，在这个模式下大幅度创新是比较危险的。</p>\r\n\r\n                <p>  所以你看，美国人是超级重视创新的。改良、革新、把产品从有做到优，这是我们擅长的事，而美国人擅长的是大跨度创新的东西，所以像IDEA奖，它就是极其重视创新的。\r\n\r\n                    （小编要特别恭喜的是，8月20日，由菲利普·斯塔克主持设计的小米MIX，因全面屏这一突破性的创新设计，最终获得IDEA金奖，至此小米今年实现了金奖大满贯！）</p>\r\n\r\n                <h4> 第三个特点：重视商业化</h4>\r\n\r\n                <p>   美国人对商业的重视，超乎我们的想象。</p>\r\n\r\n                <p>   为什么讲美国呢？我们一定要知道他，因为他是我们最大的对手。</p>\r\n\r\n                <p>   所以我们做生态链时，除了产品以外，我们超级重视这个产品能不能带来商业上的成功。所以一件事情能不能靠商业得到成功，并且具有持续性，这是我们非常重视的。\r\n\r\n                    我们去见一个行业的老先生，我问他做一家大公司最重要的内因是什么？</p>\r\n\r\n                <p>   他说吃亏。一家公司如果你知道怎么吃亏，你就有可能成为一家大公司。</p>\r\n\r\n                <p>  忽然我就想起了有人也问李嘉诚公司怎么能做大呢？他说很简单，能挣十块的时候挣八块，留一点给别人，就能长大，就能成为一个大公司。</p>\r\n\r\n                <p>  这些对我们做生态链有很大的启示：分享一些给别人，学会吃亏。其实这是一个公司非常难、非常难做到的。当它成为一个公司的基因时，你就有可能做成一家大公司。\r\n\r\n                    小米的反思和迭代</p>\r\n\r\n                <p>   2015、2016年增长缓慢，原因何在?</p>\r\n\r\n                <h4> 第一、忽视线下渠道</h4>\r\n\r\n                <p>在2014年以前，我们一直判断说整个互联网有可能达到整个中国零售的一半。如果中国零售的一半都在互联网上解决，就不用考虑线下了。那时候我们忽视了站在更高的视野上看问题。\r\n\r\n                    如果今天我们反思那个时候的话，肯定不会做这么愚蠢的判断，因为世界范围内互联网销售都只占整个零售的20%。如果能有今天这样的视野，就不会做这种误判。\r\n\r\n                    其实有错误不要紧，我们过去的七年就是在不断犯错误和改错误之间长大的。</p>\r\n\r\n                <p> 但可怕的是我们没有捕捉到一个重要的商业信号。</p>\r\n\r\n                <p> 2014年阿里投资了苏宁，这个时候阿里开始投线下。</p>\r\n\r\n                <p> 任何一个事情都有它的本源逻辑的，一个巨大的线上公司为什么要投线下，显然是它遇到了线上的阻力和压力，它是最大的电商，天塌下来肯定先砸个高的。所以，这说明他在这之前已经感受到线上销售的压力。\r\n\r\n                    我们作为一个小电商，这个信号我们没有捕捉到。2014年，如果我们迅速地知道它投资了苏宁，我们就应该马上进行总结讨论，但是我们居然忽视了，我们没有停下来反思线上有危险。\r\n\r\n                    那一年我们错过了对线下的铺设，如果那一年我们开始铺线下，我觉得今年会舒坦多了，我们至少有1000家店，这1000家店，每年流水至少有个700亿。</p>\r\n\r\n                    <h4> 第二、技术上趋于保守</h4>\r\n\r\n                <p>公司就一款手机，这一款产品卖掉千万部，我们不敢有技术上有太多前瞻性的探索。因为我们要求这部手机一定要成功，绝不能有风险。从这个细节上你能看到我们在技术上趋于保守。\r\n\r\n                    为什么保守，因为患得患失。</p>\r\n\r\n                <p>   我们没有去追求最好的技术，不是我们工程师不够强，是因为这种单品海量的商业模式，它导致了技术上的保守。于是它在技术上落伍了，探索性弱了。不是没有这样的技术实力，是商业模式的局限。</p>\r\n\r\n                        <h4>  第三、机会的错过</h4>\r\n\r\n                <p>   我们在三星退出高端机舞台的时候没抓住机会。</p>\r\n\r\n                <p>    那个时候我们处在徘徊期，正是红米巨量出货的时候，我们没有顺势去补三星的空间。</p>\r\n\r\n                <p>    但谁补了空间呢？华为。三星让出来一部分舞台，华为补进去，所以那个时候成就了华为。</p>\r\n\r\n                <h4> 第四、忽视人才梯队的建设</h4>\r\n\r\n                <p>  公司的成长跟个人的成长是一样的，有它的规律性，你在这个过程中可能会一个阶段性地违背这个规律，但是欠的债，早晚都是要还的。\r\n\r\n                <p>  比如我们强调低成本运作，强调效率，但是人才梯队的建设就会被忽视，真正扩大规模和加速的时候就上不去，因为没人。</p>\r\n\r\n                    小米今天在做哪些调整？</p>\r\n\r\n                <h4>第一、从人入手</h4>\r\n\r\n                <p>忽视了人才梯队的建设，一定是过去存在着巨大的问题。我们似乎觉得400人就能打10000人的仗，今天看起来这些想法很滑稽。\r\n\r\n                    应该这么讲，在当时，我们这么做是对的。因为一家公司要控制成本，提高效率，这样去打一仗。当时是对的，但是这个时间轴要求你早晚要还的。\r\n\r\n                    所以我们今天整个小米体系在做的是人的骨骼，就是先把梯队建设建丰满，该补的人都补上，所以我们今天在上补人的这一课。</p>\r\n\r\n                    <h4>  第二、补新零售，补线下渠道</h4>\r\n\r\n                <p>我们赶上了线下渠道再一次兴起的风口。</p>\r\n\r\n                <p> 今天你走到小米之家线下店，那家小店250平米，每年流水1亿人民币，这还不是我们最好的店，我们最好的店250平米，每年流水2亿人民币。\r\n\r\n                    一家店流水1亿人民币是什么感觉呢？</p>\r\n\r\n                <p> 整个五彩城十几万平米，一年流水14亿人民币，而我们在它的F1层250平米，10个月做到它的1/14，就是这种感觉。</p>\r\n\r\n                        <h4>第三、加紧向周边的布局</h4>\r\n\r\n                <p>比如说生态链就是从2013年开始的一个新布局。</p>\r\n\r\n                <p>如果我们2013年没有布局生态链的话，今天我们就是补新零售，到了店里也没有东西可卖。今天我们的产品组合实现了线下引流的作用。\r\n\r\n                    未来，中国3个巨大的机会</p>\r\n\r\n                <p>IOT+消费升级+新零售</p>\r\n\r\n                            <h4>  第一个机会：IOT，万物互联</h4>\r\n\r\n                <p>所有的设备都可能智能化成为电脑，这里有巨大的商业机会，这就是为什么我们做生态链这件事情。\r\n\r\n                    这个事情应该有十年二十年的时间，这是一个漫长的长跑。尽管我们有一些先发优势，但这还只是一个万里长跑中前十米的先发优势。\r\n\r\n                    我想讲一个IOT可能给渠道革命带来的机会。</p>\r\n\r\n                <p> 我们在做净化器的时候，特别想再安装一个物理按键。这个物理按键的功能就是一旦这个滤芯快用尽的时候，就呈现红色。然后你一按后面的物理按键，我明天就给你家送货。所以那时候我们特别想装这么一个物理按键。\r\n\r\n                    后来为什么没有装，因为我觉得这个事可能还早。大家可能并不习惯于使用这个键，而是更习惯于在APP上来控制它。</p>\r\n\r\n                <p>  如果设了这个键以后，又没人用，这个成本就白花了，所以我们今天这个产品，它发现该换滤芯了，然后APP上会马上给出提示，说你的滤芯快到时间了，要不要换，要换你按一个键，明天送到你家。\r\n\r\n                    我觉得这是商业上重要的一个现象，终于有一天，有一个设备，比你更早地发现你的需求。</p>\r\n\r\n                <p>当净化器用到了11月份的时候，北方开始干燥了。这个时候你在APP上收到一个提示说，家里的空气比较干燥，建议你买个加湿器，现在有小米的智能加湿器，如果想要，你按一个键，我明天就可以送到你家。\r\n\r\n                    在这个过程中，实际上是净化器把加湿器卖给你的，前提是我的净化器足够好，你买了以后的体验都是正反馈。于是乎当我向你推荐第二个产品时，你马上下单，买了个加湿器。\r\n\r\n                    你每天用加湿器，忽然有一天APP建议你加纯净水，或者买一个小米的净水器，如果你要的话，按一个键，我明天就送到你家。</p>\r\n\r\n                <p>就像“老鼠会”一样，你花钱把一个智能设备买回家，它就是一个自动贩卖机，它会一点一点地把你其他的需求都找到，然后一点一点地卖给你，我觉得这是一个新的渠道，我们叫它“老鼠会”电商，或者摇控器电商。\r\n\r\n                    过去我们的电商，第一是阿里的自由市场式电商，后来就是京东的百货公司电商，再后来就是小米的专卖店式电商。</p>\r\n\r\n                <p>我觉得可能有第四种电商形式，就是摇控器电商，或者说这种“老鼠会”电商。</p>\r\n\r\n                <p>我们买的粮食，最早是粮店的，后来粮店被超市干掉了，因为你觉得在超市买很方便。再后来超市被网购也干掉了一部分，因为你觉得网购也很方便。</p>\r\n\r\n                <p>今天更简单了，我们直接从田间地头卖到你家厨房的饭锅里，所以这是最短的路径，电商这种形式，永远是离人近的，打掉离人远的，短路径的打掉长路径的。\r\n\r\n                    所以IOT领域蕴藏着巨大的机会，它不一定完全是互联互通以及体验好的问题，它可能会有巨大的渠道机会，可能还会有渠道的大数据机会。</p>\r\n\r\n                    这是一个新的渠道，渠道在过去的一百年里都是强权的，我想在未来的二十年里一定是强权的。\r\n\r\n                    今天有很多人说，渠道为王的时代过去了，开始出现了产品为王的时代。\r\n\r\n                    其实不是，我们今天到了一个双霸主的时代，产品好和渠道好，这是一个产品和渠道博弈的时代。\r\n\r\n                    我觉得渠道作为王者，在未来十年里，二十年里，还是没问题的，这是为什么我们所有跟渠道相关的事情我们都非常重视，所有渠道属性的领域我们都非常重视。\r\n\r\n                                <h4>第二个机会：消费升级</h4>\r\n\r\n                    美国是个鼓励消费的国家。我刚到美国的时候，美国人在洗手间用纸巾，他们就弄一堆纸然后扔掉，我说这太浪费了。\r\n\r\n                    但是我现在在国内去麦当劳买吃的，点餐员给我打包的时候，抓了一把餐巾纸给我塞进去，我当时就感到，现在这一代人是在物质相对充沛的时代长大的，她没有匮乏感。\r\n\r\n                    我们原来怎么找点餐员要纸巾的呢？\r\n\r\n                    20年前我们进麦当劳，你要纸巾，人家肯定给你一张纸巾，准极了。要是两个人说给我点纸巾，他一定给你两张，然后你就怯生生地说能不能再给一张。但现在你看这个妹子，她抓了一把纸巾给你，你能感受到这是一个新时代来了。\r\n\r\n                    终于有一天，经过了三十年，我们进入了一个拐点，我们进入到一个大消费时代。\r\n\r\n                    那天晚上我激动得半宿都没睡觉，因为这是一个重要的商业信号，一代年轻人在物质充沛的时代长大，他们的观念完全不同，所以消费升级，除了我们产品的质量升级和数量升级以外，还有消费者的消费观念在升级。\r\n\r\n                    在这个巨大的消费升级时代里，只要你有好的产品，都是有巨大机会的。我们内部有个口号，我们要把消费升级当信仰，为什么叫信仰呢？信仰最重要的指标就是它的不可被置疑性。\r\n\r\n                    当你对消费升级完全信服，我相信，你制定的所有决策，才能围绕这个思路来做。所以消费升级时代，就是我们的信仰，我们拿它当信仰干，我觉得有巨大的机会。\r\n\r\n                                    <h4> 第三个机会：新零售</h4>\r\n\r\n                    在三年前的时候，尽管没有新零售这个概念，但当时我们就讲过，在未来的短则五年，长则十年的时间里，会出现大量的、高效率的线下连锁店。我觉得今天这一切都已经被验证了。\r\n\r\n                    像小米的线下店，京东也在开线下店，苏宁也在重新弄店，阿里也准备开店，各家都在开线下店。\r\n\r\n                    线下高效率的连锁店的时代就会来了，我觉得今天新渠道，线下渠道大概有几点。\r\n\r\n                    网上是这样革命的，传统电商中开始出现精品电商，像网易严选，小米有品。\r\n\r\n                    传统电商有两个最致命的问题，一是汪洋大海的选择，二是电商平台不能反向控制这些商品的品质，精品电商解决的是这两件事。有限的选择和反向控制品质，我觉得这是新零售的一个表现。\r\n\r\n                    另外，一些大的互联网巨头开始整合，像京东的新通路，阿里的零售通，用它的数据库，整合线下的零售小店，把它们混编起来，给这些小店提供数据支持。\r\n\r\n                    再有一个就是线下的这种经济型连锁店。\r\n\r\n                    它的本质是，解决传统渠道效率不够高的问题，所以才给了新的渠道新的呼声。\r\n\r\n                    所谓渠道的风口，线下渠道的热潮，它是渠道推动的吗？不是，它本质也是消费升级推动的。用户端对好的产品产生了大量的需求，它需要高效率的渠道来送达。所以它的原始动力也是消费升级带来的，并不是说渠道建设本身是没有动力的。\r\n\r\n                    我觉得未来在中国，这三个领域，持续10年-20年的时间，是有巨大机会的。你要看一看你所做的事情跟这些有没有合拍的地方，在纯市场经济的领域，大家在这三个领域里有机会。\r\n\r\n                    精彩问答\r\n\r\n                    提问：我有一个问题是关于性价比的，就是你怎么来定价，保证自己的毛利润？\r\n\r\n                    刘德：这个话题很有趣，我们要看性价比这件事背后是什么。\r\n\r\n                    第一、性价比这件事背后是效率\r\n\r\n                    只有效率高才能体现出你的性价比。光靠你的定倍率是解决不了性价比的问题的。你一定要提高效率才真正有竞争力。\r\n\r\n                    第二、我们要的是极致性价比\r\n\r\n                    极致性价比的本质是“超限战”——超越对手极限的竞争，它不是低于成本，是我低于行业的成本。因为行业效率做不了这么高，当我做这么高的时候，我击穿你，它本质是“超限战”。其实这是两个企业效率之间的对话。\r\n\r\n                    第三、心智\r\n\r\n                    当你走到小米的体系里，就会发现所有的产品性价比都高，这样的话它就成为一种心智。\r\n\r\n                    你在小米体系里可以闭着眼买东西。而不能够说像传统产业那样，出一些性价比的引流商品，然后要再出一点挣钱商品。\r\n\r\n                    我们不是，我们即使发了一万块钱的激光电视，它的性价比还是高的，因为同行要卖两三万，我们还是它的三分之一的价格。所以你要把你整个的性价比做成一个系统逻辑，你所有的产业、企业都要尊重这个逻辑。',4),(2,'沉默的十五分钟','青山刚昌','2017-08-09','<img src=\"/images/bki-20130601184414-2037036933.jpg\"><p>[1]「针对你这4年傲慢无礼的执政期，在此施下天理不容的警告。我等对你这个奸臣的死刑，早已万事具备只欠东风。朝仓知事的生杀大权，早已在我等手中。」 　　12月的某一天，获得连任的朝仓东京都知事接到了谜一般的恐吓信。信中扬言将于次日在刚刚开通的都营新地铁隧道中实行爆炸！而多亏了从毛利 小五郎那里了解到恐吓信之事的柯南，才在无人伤亡的情况下,避免了这一重大惨案的发生…… 　　是怨恨朝仓的家伙预谋已久的犯罪吗？为了追查不惜造成巨大惨案的危险罪犯，而朝仓知事在出任国土交通大臣期间建造北泽水力发电站的相关者中，难道没有人充满怨恨吗？柯南等人前往大坝的建造地新潟县…… 　　在庆祝北泽村迁移5周年的纪念典礼上人山人海，呈现了一副热闹繁华的景象。果然，犯人就藏匿在其中。在柯南正要开始调查的时候，竟然在雪原上发现了死因不明的尸体！这和地铁爆炸事件有什么联系吗？8年前的事故给村民们带来了怎样的心魔……“悲剧”牵出错综复杂的关系，如今灾难向人们袭来！！</p><p>故事剧情\r\n\r\n　引子很简单，八年前，在新潟的一个叫做北泽的小村庄，一名名叫立原冬马的小学一年级孩子被我们所熟悉的“谜之黑影”追赶，慌慌张张 地在雪地上逃命，结果脚一滑跌下山坡，血流满面地昏死过去。黑影见状离开。 　　接着就是电影标题《沉默的十五分钟》，然后是万年不变的介绍，大家看以前的剧场版即可，唯一更新的是关于滑板，嗯，这货又进化了，变成雪地旱地两用了。自不必说，是为了适应这次剧场版的背景，那个白雪皑皑的北国。 　　随着柯南经典的pose和“真相只有一个”，正戏开始。 　　在列车东都线开通的几日前，警方得到了恐吓东京都知事的恐吓信，说是要知事为曾经的胡作非为付出代价。因此在东都线开通庆典当日，目暮警部、白鸟、佐藤、高木，甚至小五郎都去做保镖。而另一边，柯南灰原还有少年侦探团，当天也跟博士一起出游。当然了，柯南的心思在恐吓事件上，一路上目不转睛地盯着手机的电视直播。可惜一进隧道，手机没信号了。 　　这时候知事和一干保镖们搭乘了首发列车。隧道的列车道在上，柯南他们所在的汽车道在下，也就是列车在柯南他们的头上跑。过隧道的时候，柯南忽然发现路边站着一个可疑的黑影拿着一个不明物体，因为车速关系一闪而过。但联想到恐吓信以及头上的列车，柯南怀疑是恐吓者要引爆隧道，手里拿的自然是引爆器。由于隧道内不能回转，博士不得不将车开出隧道后在高速公路边停下。柯南立刻将元太推下车，然后踏着滑板在车流中逆向穿梭，一边穿梭还一边打电话给目暮警部，用新一的声音要求立刻停车。当然列车闻讯紧急刹车，而柯南又举着滑板拦在路中间，将车流堵在爆炸区域之外。十五秒后炸弹引爆，列车尽管紧急停车依然被爆破流带着冲出隧道，在高速公路横冲直撞好几百米才停下来。总之，这段还是很有冲击力的。 　　之后是例行记者招待会。因为新一一如既往要求低调，所以白鸟说打电话叫停车的是警方协力者。柯南的举动，被说成受毛利小五郎的指导，嗯，当然毛利本人坐在旁边一头雾水，至于犯人，除了不明，不明，还是不明，各种不明。 　　一帮孩子也在博士家里边吃点心边看记者发布会，三个小孩还在埋怨没提到少年侦探团。柯南根据知事的资料，得知知事还在国土交通省的时候，曾经负责在新潟北泽村修建大坝。因为亲民和善的作风，几乎所有村民都同意迁移。只有一个人从头至尾反对。柯南怀疑恐吓事件与该村有关，打算去北泽村调查。三个小孩也闹着要去，灰原说如果博士去她也去——因为要“监视”博士的饮食。结果最后，不仅一帮孩子和博士去了，小兰园子毛利大叔也去了。 　　银白色的北国，疯玩，疯吃，外加博士的冷谜题。柯南独自去事务所，要北泽村和大坝的地图。在事务所碰上两名男子也来索要地图，其 中一名男子不小心将电击棒掉落在地。男子捡起来的时候解释自己经常出行野外，用来防身。柯南回去的时候，发现元太居然吃撑了去医院。女医生立原冬美开了药。众人离开后，冬美进入房间，静静地看着昏睡在床上的少年。 　　晚上，毛利大叔满怀欣喜地去泡混浴温泉，却错把猕猴当美女。小兰园子还有小朋友们则去买礼物。孩子们兴奋地讨论给小林老师带什么，熊馒头还是纪念手袋。灰原则拿起一个印有天鹅的口杯。原来反面印的字是“我爱白鸟（天鹅）！”白鸟警部和小林老师顿时打了个喷嚏。 　　在园子的怂恿下，小兰打电话给新一，问问想要什么礼物。柯南急急忙忙逃出去躲在树下接电话。于是两人背靠背地打手机。柯南蹲在树下，在雪地上写下了小兰所说的三种礼物“熊馒头，手袋，天鹅杯子”。然后柯南说那就手袋吧，说着在手袋那行字上画了个圈。 　　正聊着，三个孩子忽然兴奋地冲出旅馆。柯南马上一句“回头再聊”挂了电话。刚追出去不远，忽然想起那三行字没擦，回去已经来不及了。小兰站在树下，对着那三行字呆呆地站了许久。 　　画面一转，三个孩子居然去玩雪橇车！还飞出去了！开出村子了！更牛逼的是，还翻车把雪橇弄坏了，无奈用无线电找柯南求助，柯南又联系了旅馆的人。等待救援的过程中光彦和元太互相责备争吵起来，被柯南阻止：“语言犹如利刃，一时偏差，可能会失去一生的挚友。”运气不错，一名叫武藤的男子开车路过，将众人接回旅馆。原来武藤是来见小学同学的。一共五人，除了武藤，还有女医生冬美，旅馆负责人远野水树，以及 事务所要地图的两人，山尾和携带电击棒防身的冰川。这五人不仅是小学同学，关系还特纠结。山尾八年前出过车祸，开车撞死了一名少女，这名少女是水树的妹妹夏树。武藤就是从头至尾反对修建大坝的村民，因此一直对出卖村子的冰川怀恨在心。看着这纠结的五人，众人选择默默回房。 　　睡前小兰向园子说出了新一的电话以及雪地字迹之事，并说觉得新一一直在身边。 　　第二天清早，三个小孩又冲出去了，说要玩打雪仗。柯南和灰原虽然一向PASS，这次却被挑逗得童心大起，一帮小孩从旅馆一直打到医院。昏睡在床上的少年被欢笑声惊醒，开窗，用结结巴巴的日语要求一起玩。这名少年就是八年前坠崖昏死的冬马，昏睡八年后虽然已经十五岁，心智却只有七岁，而且对八年前的事完全没有印象。母亲冬美喜极而泣，而冬马却说“太好了，平安见到妈妈了”。 　　 一行人送冬马去健康检查，毛利大叔看到美丽的水树小姐又开始勾搭了，什么眼镜不好看啊还是隐形好啊，弄得小兰很不爽。没想到午后水树真的换了发型戴了隐形眼镜，带柯南他们去看天鹅。结果在山道上发现了死亡的冰川，电击棒也被夺走了。 　　此时，水树提到了十五分钟生命时间。在雪山中救援，从出事到发现，十五分钟决定生死。 　　新潟警方例行公事，一问都没不在场证明。 　　翌日清早，众人发现冬马在“小黑”的墓前哭。小黑是冬马很喜欢的邻家小狗，八年前发现昏迷在山坡下的冬马的就是小黑。众人安慰他的时候，忽然下起了冰晶，冬马看到冰晶似乎对以前的记忆有所反应，感到头痛。 　　吃过早饭，三个小屁孩就把冬马约出去了，说是去看看天鹅的话说不定会回忆出什么，因为八年前冬马就是拿着望远镜去看天鹅出的事柯南到冬马家发现冬马不在，却遇到了冬马的小学同学。那个女孩给柯南一幅蜡笔画，画的是小黑，说是当初冬马送给她的。画是折叠的，画面折在里面。柯南忽然想起那晚冰川说起山尾撞死人的时候，拿出了当时报道的报纸，新闻那面却是朝外。一般来说为了保护内容都会记在里面。起疑心的柯南偷偷进入冰川的房间，拿出那张纸，发现另一面记录的是东 京珠宝店抢劫杀人案，跟车祸居然是同一天。是巧合吗？柯南正在思索，外面忽然传来爆炸声，通信塔被炸毁，固话和手机都没法使用。柯南急忙用无线电召回灰原，两人驾驶滑板带着灰原去追冬马。此时冬马他们已经遭到狙击，逃入一个小山洞，这简直是自找死路。柯南和灰原气的教训了他们一顿。但事已至此无可奈何，一行人在山洞中摸索出路。柯南一边急于赶去大坝，一边说了自己的推理。 　　八年前的珠宝店抢劫杀人犯，就是山尾。山尾在东京犯事后，带着珠宝驾车逃回北泽，却在村外撞死了夏树。之后害怕被抓，暴露抢劫一事，山尾将珠宝藏匿在北泽的祖母家中，选择自首交通肇事。但是肇事现场被冬马目击，珠宝也被看到，所以他追杀冬马灭口，导致冬马坠落。没想到等他服刑归来，原来的北泽村已经被淹没在水库里。所以他接下来的计划，很有可能是炸毁大坝。至于冰川，从他收藏的那张新闻来看，他是因为得知山尾抢劫而被杀人灭口的。而在东京恐吓知事炸毁列车，当然是为了嫁祸给反对修坝，对知事怀恨在心的武藤。如今看到有记忆恢复迹象的冬马，他自然淡定不能了。 　　抵达大坝后，柯南让三个孩子陪伴冬马，灰原去找武藤，自己则去事务所通知炸弹一事。没想到事务所人员被电击棒打晕。灰原用无线电 联络，说武藤也被电击棒击晕。通话中柯南遭到山尾袭击，一路逃到大坝上。危急时刻，赶到的水树一枪击中山尾的肩头，山尾在争夺引爆器未果之后，被柯南的麻醉枪射中。 　　终于灰原，博士，武藤，冬马，以及三个孩子赶到了。冬马一看到水树不禁失声：“是那个时候的大姐姐！虽然没戴眼镜，但不会错的！” 　　“果然是这样，所以你才接受毛利大叔的建议，改发型戴隐形吧。就是为了不让冬马回忆起来。八年前追杀冬马的不是山尾，而是你。” 　　原来八年前在村口，水树与妹妹夏树争吵，失手将夏树推到路中央。恰好此时山尾驾车冲过来，造成车祸。而冬马不幸目击了。 　　“当然，我相信大姐姐虽然因为灭口而狙击冬马，但最终没有杀意。”柯南继续解释，“按刚才大姐姐的身手，不可能一路上都射偏。” 　　真相虽然大白，炸弹却爆炸了（这也是剧场版惯例，破案后平安结束的案子是没有的）。大坝开始泄漏，下游的北泽村岌岌可危。柯南让一行人先逃，自己则踏着滑板在洪流碎石间飞跃，寻找阻止毁灭村庄的方法。闻讯前来的小兰园子以及毛利大叔，看到大坝爆炸，立即调转车头。前面车在逃，后面洪水在追。小兰猛然发现了山道上疾驰的身影：“柯南！” 　　 此时柯南已经决定，靠雪崩促使洪流改道，保护村庄。但浸水的滑板出现异常，冒着黑烟停下来。愤恨不已的柯南跪在雪地上大骂“混蛋”，双拳狠狠地砸在雪地上。受到大吼震动的山体开始巨颤，雪崩爆发。柯南强制启动滑板在山道上左冲右突，企图逃避雪崩。小兰更是恐惧地大喊“快逃啊柯南君！” 　　在柯南驾驶滑板一跃而起的瞬间，他看到了从车窗探出头惊恐交加满是担忧的脸。 　　“兰……” 　　随后柯南被雪崩吞没。小兰叫得撕心裂肺。 　　雪崩成功地阻碍了洪流，挽救了村庄。一行人开始了挖掘柯南的行动。埋在雪下 的柯南虽然听到小兰的呼喊，却动不了，也出不了声，再次昏迷过去。 　　小兰拿出手机拨打柯南的电话，双手一直在颤抖：“求求你，一定要接通。” 　　来电铃声响了，大家迅速循声找寻。小兰刨出了柯南的手机，但挖得十指鲜血淋漓，也不见柯南的身影。 　　十五分钟生死时间，只剩一分钟不到。 　　绝望的小兰留着眼泪拨打了新一的号码，带着哭腔大喊：“如果你就在身边的话，请救救我们，新一！” 　　感到手机震动的柯南再次苏醒，拼尽力气按了腰带的按钮。足球破雪而出，从而告知了自己的所在。众人终于将其救出。小兰抱着柯南泪流满面。 最后，该进局子喝茶的去喝茶，该回家的回家，该买礼物的买礼物，该疗养的去疗养。毛利大叔依然催促着大家快点回东京，因为他要赶着回去看洋子的节目……over。 　　最后的最后，柯南剧场版第十六弹，制作决定！</p>',2),(3,'贝克街的亡灵','青山刚昌','2017-08-09','<img src=\"/images/bki-20130827100137-1706689881.jpg\">\r\n<h4>贝克街</h4>\r\n\r\n<p>贝克街是18世纪末期时，由喜欢投机的实业家爱德华·巴克里·普特曼设计出来，在其孙子亨利·维里亚姆·普特曼的时候建成的。贝克街的名字来自于普特曼家长年的朋友——爱德华·贝克的姓氏。</p>\r\n\r\n<p>虽然现在的贝克街是位于伦敦西部的一条非常长的南北街，但是19世纪末的贝克街却比现在的短，只有400米的长度。南端连接以大地主普特曼之名命名的普特曼·斯克爱尔，北侧则从约克不列斯开始通过阿帕贝克街，直达里捷得帕克。</p>\r\n\r\n如果说贝克街有名的地方是哪里，那当数福尔摩斯所住的221b，但是当时的贝克街东侧的南到北是1号~42号，西侧的北到南是44号~88号，221号根本就不存在。也就是说221b是作者凭空所想象出的一个地方。221b中的b不是字母表中的“b”而是法语中的bis（~的2）的意思，它在221门牌号中是表示2世代目的补助型记号。现在的贝克街221b是金融会社（ABBY NATIONAL），跟它相邻的就是夏洛克·福尔摩斯博物馆。</p>\r\n\r\n<h4>开膛手杰克</h4>\r\n\r\n<p>开膛手杰克是英国犯罪史上非常著名的连续杀人事件的犯人。1888年8月31日开始到11月9日约2个月间在依斯特爱德的呼瓦特路附近有5个妓女被杀害了。是被母亲遗弃（疑似他母亲是妓女），警方对凶手所留信件上的血迹进行了DNA检测，发现为女性。在案发现场留下的一对戒指被认为是儿时他与母亲的，他憎恨母亲当年遗弃他的行为，除却五人之外，他还杀死了自己的母亲。</p>\r\n\r\n<p>被害者为以下5人\r\n\r\n喉和阴部被切开2次。\r\n\r\n喉被切开，头部和躯体用一张皮连在一起，下腹部被切开，子宫和膀胱被拿走。\r\n\r\n鼻子和右耳朵被切，子宫和左肾脏被拿走。脚被切断，被切的鼻子和乳房被整齐摆放着。还有，被拽出来的小肠在壁画上以同样的条纹挂起来。\r\n\r\n第4次的犯案后，警察接到了一封信件，上面写着没有公开的犯案内容和杰克的名字。以后犯人就被称为开膛手杰克。第3次时，被害者的肾脏和信件被送到了自卫团的团长家，信上写道“剩下的做成油炸食品吃了”。\r\n\r\n虽然警察对最有力的5个嫌疑者进行了再三的取证调查，但因为没有找到决定性的证据，最终搜查终止了。经过了100年以上的今天也仍然不能判断出真正的犯人是何人。\r\n\r\n阿瑟·柯南·道尔爵士和威廉·史都华（William Stewart）提出是名女性凶手扮成开膛手杰克。英国学者曾经用现代的DNA技术提取被认为是开膛手杰克所书写的信上所残留的DNA经过对比证明了写为同一个女人所写。该理论的支持者相信该名女性凶手的工作是助产士，她能在不引起注意和嫌疑的情况下身著沾满血腥的衣服，而且比男人更容易获得受害者的信任。</p>\r\n\r\n<h4>诺亚方舟</h4>\r\n\r\n<p>人工智能的名称被誉为Noah\'s Ark（诺亚方舟）。这是旧约圣经的6章开始到10章所记载着的话，因为这是旧约圣经中最有名的一段话，所以即使没有听过那些内容，光听过那名字的人就非常多了。</p>\r\n\r\n<p>主宰之神（创造天地的耶和华）因为地面住满了人，而感叹其间满载着的罪恶和不法的事情，想过要把所有的一切毁灭，但后来还是传达给了坠落在地上的唯一的“神的随从”——诺亚，让诺亚建造方舟，命令他把地上所有的生物每每一组乘坐在方舟里。方舟的建材使用的是坚硬的羽叶棕树。方舟全长300anm，幅50anm，高30anm。1anm是从肘到中指尖的长度（约45cm）换算一下的话，方舟的全长135m，幅22.5m，高13.5m。</p>\r\n\r\n<p>方舟完成的7日后，耶和华让大雨持续了40日，并引起了洪水，让地面被冲洗了一便。因为洪水的原因，鸟兽还有人类全部死亡。雨停后，诺亚为了调查引水之事放飞了鸽子，但是第一次因为没有找到休息翅膀的地方，所以回来了。7日后再次放飞鸽子，这次叼着橄榄叶回来了，接下来的7日后再一次放飞了鸽子，这次却没有回来。至此知道了地面引水之事的诺亚决定再次回到地面。但是耶和华却把“不想再因为第2次的洪水灭掉所有的生物了。”这样的契约和诺亚还有他的子孙，以及所有的生物联系在了一起。</p>\r\n\r\n<img src=\"/images/bki-20130827095705-1735596573.jpg\" class=\"pull-right\">\r\n<p>大本钟是伦敦一处标志性建筑。在茧内表示留下的孩子人数的那个Big Ben，Big Ben这个大钟别看只是独立在一处的一个物像，它可是代表伦敦的艺术型建筑，在英国议会大厦上高耸着，当初建成住所兼会议所的议会大厦从爱德华1世~亨利8世，历代的王都把它当做王宫来一直使用着。顺便说一下，建筑物长300米，房间在1000个以上。</p>\r\n\r\n<p>钟表高约95米，钟的重量约13.5吨。《Big Ben》这名字是在，1895年钟塔再建时，工事负责人“本夏敏·福尔”的爱称中来的。他的身体非常庞大，所以大家平日里都称呼他为“Big Ben”，在给钟起名字的时候，由于“Big Ben不错呀”这个话，而就变成了那名字。顺便说一下，“Big Ben”正确的说，不是指“钟塔”，而是指那个“钟”，这个钟响起时有很容易让人记住的音色。无论是谁第一次听的时候因为是“叮-咚-当”地做响，也就以为是学校的铃声。在剧中被当作参加游戏剩下孩子的标志。</p>\r\n\r\n<h4>莫里亚蒂教授</h4>\r\n\r\n<p>莫里亚蒂教授是伦敦“犯罪界的拿破仑”，福尔摩斯曾称他“像是一只位于网中的蜘蛛，任何一丝牵动都逃不过他的眼睛”，他也是一位数学天才。他于二十一岁时发表了一篇颠覆数学界的论文，原本前程似锦，但他祖先犯罪的血液开始影响他，所以伦敦有一半的犯罪事件都是由他主导的。</p>',3),(4,'共享单车押金真的难退吗？是技术问题，还是故意为之！','默认栏目','2017-08-29','<p>昨天是七夕，晚上的时候发现地铁的人流比平时多出了好几倍，这还不重要，关键都是情侣或小两口，并且正在筹划着晚上的活动，这可把一些单身狗虐的苦不堪言！</p>\r\n<p>本是一个值得庆祝、幸福的日子，情侣可以看看电影、吃个饭，关键还可以啪啪啪...，小两口可以借此时机，好好放纵一回。然而有些人就没那么幸福了，过不了七夕也就算了，还憋了一肚子气。</p>\r\n<img src=\"/images/59a5266d3ff71.jpg!m1024\"/>\r\n<p>据南方都市报报道，因先前“土豪金”外形而出名的酷奇共享单车，近期因用户反映押金难退问题被推上了舆论的风口。并且有用户表示，申请退押金后20多天过去了都没有收到任何退款信息。而官方给的解释却是因系统升级敷衍过去。</p>\r\n<h4>共享单车押金难退已成通病</h4>\r\n<p>目前共享单车市场已是鱼龙混杂、乱象丛生，已经不是三言两语就能够解决掉的，而因为押金难退引起的风波也已经不是第一次发生了，几乎所有的共享单车品牌都遇到过这类困境，早前因OFO的押金难退问题被传的沸沸扬扬，虽然如今已经解决，现在如果通过OFO充值后，想退押金的话，几乎都是秒退，但是随之而来的是其它共享单车品牌陆陆续续出现类似的押金难退的问题。</p>\r\n<p>2016年7月在广州注册成立的小鸣单车前段时间就被盯上了，据网友反映，小鸣单车在使用过程中出现押金退款失败的问题，申请了半个月都没有拿到，而官方给的回应是通过绿色通道正在处理。至此，我不经就要问了，押金退款真的需要这么久吗？哪怕确实是技术问题，因用户数据庞大，无法及时处理或需要时间，但是也不用那么长时间吧！及时有个别特殊情况，是不是应该提前给用户一个短信或者微信说明，这样也会让用户安心，涉及一个基本的用户体验问题。</p>\r\n<p>另外，有了早期的这种因技术问题导致的押金退还难的问题发生后，应该后面来的单车企业汲取经验，进行整改、技术的升级，为何摩拜、OFO能够及时的解决，似乎押金难退成了共享单车企业的通病。</p>\r\n<h4>因技术问题导致押金难退是借口</h4>\r\n<p>“只要站在风口，猪也能飞起来”这是雷军在40岁那年所领悟的生命密语。从那以后不知有多少人把这句话当作创业的借口、和精神支柱，而没有从深层次去理解这句话的含义，小米之所以成功，除了一些偶然的因素外，其实更多的是其必然的因素。</p>\r\n<p>然而，有些人只是看到了其偶然因素，盲目的跟风创业。就比如共享单车，以为站对了风口，就真的可以飞上天了。然而当做了之后，突然明白，童话里都是骗人的，此时不仅是需要技术的问题，更多是需要大量的资金来撑起这个市场，而自己的资金已是捉襟见肘，又得不到融资，但是又投资了那么多心血又不能不做下去，就会打起了其它的注意。此时，用户的押金成为了其首要的大量对象。</p>\r\n<p>虽然上面论述的真实性有待考量，但也不代表不无可能，比如今年2月份才成立的共享单车品牌3Vbike，它的启动资金只有少的可怜的70万元，在摩拜、OFO接连融资上亿美金的时期，这么少的启动资金就敢进入市场，除了说具有勇气之外，还能说什么。因此在资金不足、又得不到融资的情况下，只能挪用押金进行缓解了。</p>\r\n<img src=\"/images/59a63d84069ee1504066948.png\">\r\n<p>另外，摩拜单车公关部相关负责人去年10月曾向《法治周末》表示，公司从财务角度出发，对这笔资金也会进行一些较为稳妥的操作，比如购买一些风险较低的理财产品，但这样做的目的纯粹是为了保值，并非是为了盈利。虽然摩拜表明并非为了盈利，但我们无法保证其它的一些体量比较小的共享单车企业，不动心思。因此在用户退还押金的时候，因为资金周转或其它的一些因素导致押金不能及时退还。</p>\r\n<h4>市场监管混乱</h4>\r\n<p>我们深入了解的话不难发现，行业内的押金管理方式呈多样化，普遍的都是存于自己的账户中。也就是说，目前市场上对于共享单车的押金如何管理没有一个确定统一的管理方式，企业可以随时挪用这笔资金，并且无需经过任何手续。那么就会导致上面所说的一些中小企业用来作为周转资金来使用了。</p>\r\n<p>另外，虽然摩拜单车日前已表示，已经开设了专门存放押金的银行账户，100%确保安全，但也还是存在于摩拜账户下，仍然可以随时动用，还是存在一定的安全风险，也就是你把钱借个陌生人，而中间却没有担保人一样。</p>\r\n<p>对于目前来说，能够确保这笔资金安全的最好方式，就是由第三方平台保存，也就是银行。而这一次酷奇单车押金难退事件，相关部门就规定通过A PP向社会公开注册地开立用户押金需由银行来进行资金的监管，并且，据易观互联网交通出行研究中心分析师表示，目前押金都是由单车运营方管理，交给第三方监管会让用户更加放心，“如果能够定期公布这笔钱的流向会更好。”</p>\r\n<p>因此，鉴于目前共享单车押金难退的问题，我觉得其根本原因并不在于技术上面，而还是在于共享单车企业、和市场监管的问题。</p>\r\n<p>【陈剑锋，科技自媒体人、专栏作者，专注于科技、移动互联网的发展。微信公众号：陈剑锋(QQ784580609)】</p>',5);

/*Table structure for table `t_blog` */

DROP TABLE IF EXISTS `t_blog`;

CREATE TABLE `t_blog` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `chickhit` bigint(20) DEFAULT NULL,
  `content` text,
  `keyword` varchar(255) DEFAULT NULL,
  `releasedate` datetime DEFAULT NULL,
  `replyhit` bigint(20) DEFAULT NULL,
  `summary` varchar(255) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `typeid` bigint(20) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FKn0ympwhdyp3t5xbd21yn0n5jb` (`typeid`)
) ENGINE=MyISAM AUTO_INCREMENT=31 DEFAULT CHARSET=utf8;

/*Data for the table `t_blog` */

insert DELAYED  into `t_blog`(`id`,`chickhit`,`content`,`keyword`,`releasedate`,`replyhit`,`summary`,`title`,`typeid`) values (1,NULL,'<p><a target=\"_blank\" href=\"https://baike.baidu.com/item/linux\" style=\"color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);\">linux</a><span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; text-indent: 28px;\">命令是对Linux系统进行管理的命令。对于Linux系统来说，无论是中央处理器、内存、</span><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E7%A3%81%E7%9B%98%E9%A9%B1%E5%8A%A8%E5%99%A8/5900722\" data-lemmaid=\"5900722\" style=\"color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);\">磁盘驱动器</a><span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; text-indent: 28px;\">、键盘、鼠标，还是用户等都是文件，Linux系统管理的命令是它正常运行的核心，与之前的DOS命令类似。linux命令在系统中有两种类型：内置Shell命令和Linux命令。</span><br></p>',NULL,'2018-09-25 17:30:06',NULL,'摘要：linux命令是对Linux系统进行管理的命令。对于Linux系统来说，无论是中央处理器、内存、磁盘驱动器、键盘、鼠标，还是用户等都是文件，Linux系统管理的命令是它正常运行的核心，与之前的DOS命','linux命令',2),(3,NULL,'<h3 style=\"outline: 0px; margin-top: 8px; margin-bottom: 16px; font-size: 22px; color: rgb(79, 79, 79); font-weight: 700; line-height: 30px; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\">1、下载：</h3><p style=\"outline: 0px; margin-bottom: 16px; font-size: 16px; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\">https://github.com/wenzhixin/bootstrap-table<br style=\"outline: 0px; word-break: break-all;\"></p><h3 style=\"outline: 0px; margin-top: 8px; margin-bottom: 16px; font-size: 22px; color: rgb(79, 79, 79); font-weight: 700; line-height: 30px; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\"><a name=\"t1\" style=\"outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>2、文档：</h3><p style=\"outline: 0px; margin-bottom: 16px; font-size: 16px; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\">http://bootstrap-table.wenzhixin.net.cn/zh-cn/documentation/<br style=\"outline: 0px; word-break: break-all;\"></p><h3 style=\"outline: 0px; margin-top: 8px; margin-bottom: 16px; font-size: 22px; color: rgb(79, 79, 79); font-weight: 700; line-height: 30px; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\">3、引用：</h3><pre><code class=\"language-html hljs xml\"><ol class=\"hljs-ln\"><li><div class=\"hljs-ln-numbers\"><div class=\"hljs-ln-line hljs-ln-n\" data-line-number=\"1\"></div></div><div class=\"hljs-ln-code\"><div class=\"hljs-ln-line\"><span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">link</span> <span class=\"hljs-attr\">rel</span>=<span class=\"hljs-string\">\"stylesheet\"</span> <span class=\"hljs-attr\">href</span>=<span class=\"hljs-string\">\"bootstrap.min.css\"</span>&gt;</span></div></div></li><li><div class=\"hljs-ln-numbers\"><div class=\"hljs-ln-line hljs-ln-n\" data-line-number=\"2\"></div></div><div class=\"hljs-ln-code\"><div class=\"hljs-ln-line\"><span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">link</span> <span class=\"hljs-attr\">rel</span>=<span class=\"hljs-string\">\"stylesheet\"</span> <span class=\"hljs-attr\">href</span>=<span class=\"hljs-string\">\"bootstrap-table.css\"</span>&gt;</span></div></div></li><li><div class=\"hljs-ln-numbers\"><div class=\"hljs-ln-line hljs-ln-n\" data-line-number=\"3\"></div></div><div class=\"hljs-ln-code\"><div class=\"hljs-ln-line\"> </div></div></li><li><div class=\"hljs-ln-numbers\"><div class=\"hljs-ln-line hljs-ln-n\" data-line-number=\"4\"></div></div><div class=\"hljs-ln-code\"><div class=\"hljs-ln-line\"><span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">script</span> <span class=\"hljs-attr\">src</span>=<span class=\"hljs-string\">\"jquery.min.js\"</span>&gt;</span><span class=\"undefined\"></span><span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">script</span>&gt;</span></div></div></li><li><div class=\"hljs-ln-numbers\"><div class=\"hljs-ln-line hljs-ln-n\" data-line-number=\"5\"></div></div><div class=\"hljs-ln-code\"><div class=\"hljs-ln-line\"><span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">script</span> <span class=\"hljs-attr\">src</span>=<span class=\"hljs-string\">\"bootstrap.min.js\"</span>&gt;</span><span class=\"undefined\"></span><span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">script</span>&gt;</span></div></div></li><li><div class=\"hljs-ln-numbers\"><div class=\"hljs-ln-line hljs-ln-n\" data-line-number=\"6\"></div></div><div class=\"hljs-ln-code\"><div class=\"hljs-ln-line\"><span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">script</span> <span class=\"hljs-attr\">src</span>=<span class=\"hljs-string\">\"bootstrap-table.js\"</span>&gt;</span><span class=\"undefined\"></span><span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">script</span>&gt;</span></div></div></li><li><div class=\"hljs-ln-numbers\"><div class=\"hljs-ln-line hljs-ln-n\" data-line-number=\"7\"></div></div><div class=\"hljs-ln-code\"><div class=\"hljs-ln-line\"><span class